INSERT INTO `testAdmin_query_builder` (`query_id`, `query_category`, `query_name`, `query_description`, `query_string`, `query_keys_list`) VALUES
(1, 'email', 'Todos los clientes (Con o sin suscripcion)', 'Todos los clientes..', 'select customers_email_address, customers_firstname, customers_lastname from TABLE_CUSTOMERS order by customers_lastname, customers_firstname, customers_email_address', ''),
(2, 'email,newsletters', 'Customer Account Newsletter Subscribers', 'Returns name and email address of newsletter subscribers who have a customer account.', 'select customers_firstname, customers_lastname, customers_email_address from TABLE_CUSTOMERS where customers_newsletter = \'1\'', ''),
(3, 'email,newsletters', 'Clientes inactivos (>3meses) (Suscriptores)', 'Subscriptores que tienen alguna compra, pero no han comprado en los ultimos tres meses.\r\n', 'select o.date_purchased, c.customers_email_address, c.customers_lastname, c.customers_firstname from TABLE_CUSTOMERS c, TABLE_ORDERS o WHERE c.customers_id = o.customers_id AND c.customers_newsletter = 1 GROUP BY c.customers_email_address HAVING max(o.date_purchased) <= subdate(now(),INTERVAL 3 MONTH) ORDER BY c.customers_lastname, c.customers_firstname ASC', ''),
(4, 'email,newsletters', 'Clientes activos los ultimos 3 meses (Suscritos)', 'Newsletter subscribers who are also active customers (purchased something) in last 3 months.', 'select c.customers_email_address, c.customers_lastname, c.customers_firstname from TABLE_CUSTOMERS c, TABLE_ORDERS o where c.customers_newsletter = \'1\' AND c.customers_id = o.customers_id and o.date_purchased > subdate(now(),INTERVAL 3 MONTH) GROUP BY c.customers_email_address order by c.customers_lastname, c.customers_firstname ASC', ''),
(5, 'email,newsletters', 'Clientes activos en los ultimos 3 meses (Independientemente de la condicion de s', 'Clientes activos en los últimos 3 meses (Independientemente de la condición de suscripción)', 'select c.customers_email_address, c.customers_lastname, c.customers_firstname from TABLE_CUSTOMERS c, TABLE_ORDERS o WHERE c.customers_id = o.customers_id and o.date_purchased > subdate(now(),INTERVAL 3 MONTH) GROUP BY c.customers_email_address order by c.customers_lastname, c.customers_firstname ASC', ''),
(6, 'email,newsletters', 'Administradores', 'Just the email account of the current administrator', 'select \'ADMIN\' as customers_firstname, admin_name as customers_lastname, admin_email as customers_email_address from TABLE_ADMIN where admin_id = $SESSION:admin_id', ''),
(7, 'email,newsletters', 'Los clientes que nunca han completado una compra', 'Los clientes que nunca han completado una compra', 'SELECT DISTINCT c.customers_email_address as customers_email_address, c.customers_lastname as customers_lastname, c.customers_firstname as customers_firstname FROM TABLE_CUSTOMERS c LEFT JOIN  TABLE_ORDERS o ON c.customers_id=o.customers_id WHERE o.date_purchased IS NULL', ''),
(8, 'email,newsletters', 'Todos los suscriptores del newsletter (Clientes mas solo suscriptores)', 'Todos los suscriptores del boletín', 'select c.customers_firstname, c.customers_lastname, s.email_address as customers_email_address from TABLE_SUBSCRIBERS as s left join TABLE_CUSTOMERS as c on c.customers_id = s.customers_id ', ''),
(9, 'email,newsletters', 'Solo los suscritos al newsletter (no es cliente)', 'Returns email address of all confirmed Newsletter-Only subscribers.', 'SELECT email_address as customers_email_address FROM TABLE_SUBSCRIBERS WHERE email_format != \'NONE\' and confirmed = 1 and (customers_id IS NULL or customers_id = 0) order by email_address', ''),
(10, 'email,newsletters', 'Grupo de Pruebas', 'Returns name and email address of Newsletter-only subscribers designated in Email test group configuration.', 'SELECT s.email_address as customers_email_address FROM TABLE_SUBSCRIBERS as s LEFT JOIN TABLE_CONFIGURATION as q on LOCATE( s.email_address, q.configuration_value) >= 1 WHERE configuration_key = \'NEWSONLY_SUBSCRIPTION_TEST_GROUP\' ', ''),
(11, 'email,newsletters', 'Email Test Group - Customers', 'Returns name and email address of Newsletter-only subscribers designated in Email test group configuration.', 'SELECT c.customers_email_address as customers_email_address FROM TABLE_CUSTOMERS as c LEFT JOIN TABLE_CONFIGURATION as q on LOCATE( c.customers_email_address, q.configuration_value) >= 1 WHERE configuration_key = \'NEWSONLY_SUBSCRIPTION_TEST_GROUP\' ', ''),
(14, 'email,newsletters', 'Customers que hayan realizado algun pedido', 'Customers que hayan realizado algun pedido', 'select distinct customers_email_address, customers_name from TABLE_ORDERS order by customers_name, customers_email_address', ''),
(15, 'email,newsletters', 'Subscribers from Home Page', 'Newsletter subscribers from Home Page', 'select \'Dear\' as customers_firstname, \'Subscriber\' as customers_lastname, email as customers_email_address from TABLE_BSK_NEWSLETTER_SUBSCRIBERS', '');
